let nNodes,nodes,rSq,t,theme;function setup(){let e=document.getElementById("sidebar-sketch");if(e===null){noLoop();return}let n=createCanvas();n.parent(e),windowResized(),updateTheme(),nNodes=random(10,50),nodes=[],rSq=5e3,t=0;for(let e=0;e<nNodes;e++)nodes.push(createVector())}function mouseWheel(e){e.delta>0&&nodes.length<100?nodes.push(createVector()):nodes.pop()}function draw(){background(theme.bg);for(let e=0;e<nodes.length;e++){node=nodes[e],node.x=width*noise(t+e),node.y=height*noise(t+e+5);for(let t=e+1;t<nodes.length;t++){let n=(node.x-nodes[t].x)**2+(node.y-nodes[t].y)**2;n<rSq&&drawEdge(node,nodes[t],n)}drawNode(node)}t=t+5e-4}function drawNode(e){stroke(theme.body),fill(theme.body),circle(e.x,e.y,2)}function drawEdge(e,t,n){theme.a.setAlpha(255*(1-n/rSq)),stroke(theme.a),line(e.x,e.y,t.x,t.y)}function updateTheme(){let e=getComputedStyle(document.body);theme={bg:color(e.getPropertyValue("--bg-color")),bgSecondary:color(e.getPropertyValue("--secondary-bg-color")),a:color(e.getPropertyValue("--a-color")),body:color(e.getPropertyValue("--body-color"))}}document.addEventListener("DOMContentLoaded",function(){let e=document.querySelector(".theme-switch");e.addEventListener("click",updateTheme,!1)},!1);function windowResized(){if(windowWidth<=950){noLoop();return}loop();let e=document.getElementById("sidebar-sketch"),{width:t,height:n}=e.getBoundingClientRect();resizeCanvas(t,n)}